 private const val FOLLOW_UP_CHANNEL_ID = "FollowUpAlarmChannel"
 private const val PILLBOX_REMINDER_CHANNEL_ID = "PillboxReminderChannel"

 private fun createBaseNotificationChannel(
 context: Context,
 channelId: String,
 name: String,
 description: String
 ) {
 if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.O) {
 val alarmUri = Uri.parse("android.resource://${context.packageName}/${R.raw.alarm_sound}")
 val audioAttributes = AudioAttributes.Builder()
 .setUsage(AudioAttributes.USAGE_ALARM)
 .build()

 val channel = NotificationChannel(channelId, name, NotificationManager.IMPORTANCE_HIGH).apply {
 this.description = description
 setSound(alarmUri, audioAttributes)
 }

 context.getSystemService(NotificationManager::class.java)?.createNotificationChannel(channel)
 }
 }

 private fun createNotificationChannel(context: Context) = createBaseNotificationChannel(
 context,
 CHANNEL_ID,
 "PillReminderChannel",
 context.getString(R.string.channel_for_reminding_users_to_take_their_medicines)
 )

 private fun createFollowUpNotificationChannel(context: Context) = createBaseNotificationChannel(
 context,
 FOLLOW_UP_CHANNEL_ID,
 "PillReminderFollowUpChannel",
 context.getString(R.string.channel_for_reminding_users_to_take_their_medicines)
 )

 private fun createPillboxNotificationChannel(context: Context) = createBaseNotificationChannel(
 context,
 PILLBOX_REMINDER_CHANNEL_ID,
 "PillboxReminderChannel",
 "Channel for reminding users to refill their pillboxes"
 )

 private fun buildBaseNotification(
