 .setPriority(NotificationCompat.PRIORITY_MAX)
 .setContentIntent(pendingIntent)
 .apply {
 snoozeIntent?.let {
 addAction(0, context.getString(R.string.snooze_alarm), it)
 }
 if (actionName != null && actionIntent != null) {
 addAction(0, actionName, actionIntent)
 }
 }
 .build()
 }

