 popupMenuView.isAccessible = true
 val menu = popupMenuView.get(popup)
 menu?.javaClass?.getDeclaredMethod("setBackgroundDrawable", Drawable::class.java)
 ?.invoke(menu, ColorDrawable(ContextCompat.getColor(requireContext(), R.color.your_desired_color)))
