 .setContentTitle(title)
 .setContentText(content)
 .setAutoCancel(true)
 .setSmallIcon(R.drawable.ic_pill)
 .setLargeIcon(largeIcon)
 .setPriority(NotificationCompat.PRIORITY_MAX)
